import axios from "axios";

const axiosWithAuth = () => {
  const token = localStorage.getItem("token");

  return axios.create({
    headers: {
      Authorization: `${token}`,
    },
    baseURL: "https://potluck-planner1220.herokuapp.com/",
  });
};

export default axiosWithAuth;
