function ConfirmAttendance(props) {

  const [attending, setDate] = useState({title: props.event.title, date: props.event.date, time: props.event.time});

  return (  
    <div className="RSVP">
        {props.rsvp ? <p>You have confirmed you will be attending this event</p> : <p>You have not yet confirmed you will be attending this event</p>}

    </div>
  );
}

export default ConfirmAttendance;
