import {useEffect, useState} from 'react';

function DisplayDate(props) {

  const [date, setDate] = useState({date: props.event.title, date: props.event.date, time: props.event.time});
  const getData = () =>{
    //when endpoint is set up the data will be pulled from database, for now I am setting it statically. 
    setDate({title: 'Family Dinner', date: '11/25', time: '12:00pm'})
  }
  useEffect(getData, []);
  return (  
    <div className="date">
      <h2>Event Name: {date.title}</h2>
      <h2>Date: {date.date}</h2>
      <h2>Time: {date.time}</h2>
      <button onClick={props.changeEdit}>Edit</button>
    </div>
  );
}

export default DisplayDate;
